//<script> tag needs to be before main.js class from the original html


//All components.js need to be referenced in this components object as "componentClassName: componentClassName", and also need to have their <script> references in mainHtml
const components = {
    newTimerComponent: newTimerComponent
}


window.onload = function includeHTML() {
    var z, i, elmnt, file, xhttp;
    /* Loop through a collection of all HTML elements: */
    z = document.querySelectorAll("[inject-html-component]");
    
    for (i = 0; i < z.length; i++) {
      elmnt = z[i];
      /*search for elements with a certain atrribute:*/
      componentName = elmnt.getAttribute("inject-html-component");
      
      if (componentName) {
          elmnt.innerHTML = components[componentName].html;
          /* Remove the attribute, and call this function once more: */
          elmnt.removeAttribute("inject-html-component");
      }
    }
  }