//window.dispatchEvent(new CustomEvent("componentReady"))
function build() {
  buildComponentsScheme();
  innerComponents();
};

var componentsScheme={};
function buildComponentsScheme() {
  componentsScheme = {
    [baseComponentName]: eval(baseComponentName)
  };
  for (index in componentsNames) {
    componentsScheme = {
      ...componentsScheme,
      [componentsNames[index]]: eval(componentsNames[index]),
    };
  }
}

function innerComponents() {
  var z, i, elmnt, file, xhttp;
  /* Loop through a collection of all HTML elements: */
  z = document.querySelectorAll("[inject-html-component]");

  for (i = 0; i < z.length; i++) {
    elmnt = z[i];
    /*search for elements with a certain atrribute:*/
    componentName = elmnt.getAttribute("inject-html-component");

    if (componentName) {
      elmnt.innerHTML = componentsScheme[componentName].html;
      /* Remove the attribute, and call this function once more: */
      elmnt.removeAttribute("inject-html-component");
    }
  }
}
