const componentsRoute = "../../js/components/";
//All componentClassName.js need to be referenced in this components object as "componentClassName"
const componentsNames = ["newTimerComponent"];
const baseComponentName = "baseComponent";
const frameworkScriptRoute = "../libs/mushuSax/InjectJsComponents.min.js";


var scriptsLoaded = false;
window.addEventListener("load", function innerScriptComponentsReferences() {
  if(!scriptsLoaded) {
    loadScripts().then(_ => {
      build();
    })
  }
});

function loadScript(url) {
  return new Promise((resolve, reject) => {
    const script = document.createElement('script')
    script.src = url
    document.head.appendChild(script)
    script.onload = () => resolve()
  })
}

async function loadScripts() {
  await loadScript(componentsRoute + baseComponentName + `.js`)
  await loadScript(componentsRoute +
    componentsNames[0] +
    `/` +
    componentsNames[0] +
    `.js`)
    await loadScript(frameworkScriptRoute)
  // ..
}


